import axios from 'axios';

const url = 'https://covid19.mathdro.id/api'

export const fetchData= async ()=>{
    try {
        const {data:{confirmed, recovered,deaths, lastUpdated}}= await axios(`${url}`);
        return{confirmed, recovered,deaths, lastUpdated}

    } catch (error) {
        console.log(error);
        
        
    }
}
